import React, { Component } from 'react';
import { FaPlus, FaEdit, FaWindowClose } from 'react-icons/fa';
import './Main.css';

export default class Main extends Component {
  state = {
    novatarefa: '',
    tarefas: [],
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const { tarefas, novatarefa } = this.state;
    const tarefa = novatarefa.trim();

    if (!tarefa || tarefas.includes(tarefa)) return;

    this.setState({
      tarefas: [...tarefas, tarefa],
      novatarefa: '',
    });
  };

  handleChange = (e) => {
    this.setState({
      novatarefa: e.target.value,
    });
  };

  render() {
    const { novatarefa, tarefas } = this.state;

    return (
      <div className="main">
        <h1>Lista de Tarefas</h1>
        <form onSubmit={this.handleSubmit} className="form">
          <input
            value={novatarefa}
            onChange={this.handleChange}
            type="text"
          />
          <button type="submit">
            <FaPlus />
          </button>
        </form>
        <ul className="tarefas">
          {tarefas.map((tarefa) => (
            <li key={tarefa}>
              {tarefa}
              <span>
                <FaEdit onClich={this.handDelete} className="edit" />
                <FaWindowClose onClich={this.handDelete} className="delete" />
              </span>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}
